//node.warn(msg.payload.idx)

if (msg.payload.idx===1704) {  // Silent Mode
    node.warn(msg.payload);

    var silentMode = "0x01";

    if (msg.payload.svalue1 == "0")    {
        silentMode = "0x01";   
    } else if (msg.payload.svalue1 == "10")    {
        silentMode = "0x02";
    } else if (msg.payload.svalue1 == "20")    {
        silentMode = "0x03";
    } else if (msg.payload.svalue1 == "30")    {
        silentMode = "0x04";
    }
    if (silentMode != context.global.panasonic.wpSilentMode){
            context.global.panasonic.wpValueChanged = 1;
            context.global.panasonic.wpSilentMode = silentMode;                
    }


} else if (msg.payload.idx===1702) {  // TargetTemp

    node.warn(msg.payload);

    var TempInDec = parseInt(msg.payload.svalue1);

    if ((TempInDec > 19) && (TempInDec < 56)) {
        var TempInHex = "0x" + parseInt(TempInDec+128,10).toString(16).toUpperCase();

        if (TempInHex != context.global.panasonic.wpTargetTemp){
            context.global.panasonic.wpValueChanged = 1;
            context.global.panasonic.wpTargetTemp = TempInHex;                
        }

    }


} else if (msg.payload.idx===1703) {      // OnOff

    node.warn(msg.payload);
    var switchOnOff = "0x0" + (msg.payload.nvalue + 1);

    if (switchOnOff != context.global.panasonic.wpOnOff){
            context.global.panasonic.wpValueChanged = 1;
            context.global.panasonic.wpOnOff = switchOnOff;                
    }

} else {
 //   node.warn("other device")
}
return null;
